package week1.day1;

public class NegativePositiveNumber {
    //Convert a negative number to positive number
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int number = -40;
		
		  if(number > 0)
	        {
	            System.out.println(number+" is a positive number");
	        }
	        else if(number < 0)
	        {
	        	number = number *-1;
	            System.out.println(number+" is converted positive number");
	}

	}

}
